import { useEffect, useState } from 'react';
import '../App.css';
const Twit = require('twit');


function News() {

    const Twitter = new Twit({
        consumer_key: "cv3LdzhSlpU9jMn1xho0FRWQi",
        consumer_secret: "1oaLyX3rELwCzlMejWLlp425YJiSbU9Y3h29pp1R0Snju1yY2S",
        access_token: "1485896912-TjtmnAtr3ztIcNYXXUIw4OAE0bF5yoH2w8a9OZp",
        access_token_secret: "1485896912-TjtmnAtr3ztIcNYXXUIw4OAE0bF5yoH2w8a9OZp",
    });

    const [Tweets, setTweets] = useState('Loading...');

    useEffect(() => {
        Twitter.get('search/tweets', { q: 'banana since:2011-07-11', count: 100 }, function (err, data, response) {
            if (err) {
                console.log(err);
            }
            else {
                response.data.statuses.map(tweet => {
                    setTweets(tweet.text);
                })
            }
        })
    }, []);

    return (
        <div className="Widget News">
            <p>
                {Tweets}
            </p>
        </div>
    )
}

export default News;
