import { useEffect, useState } from 'react';
import '../App.css';
const needle = require('needle');


function News() {
    const token = "AAAAAAAAAAAAAAAAAAAAALjMdQEAAAAA4W09iav1RxWbqapsKhbL0Al6rrU%3DWur6GkBuFKtGFmpjDX0mn90uxTN4yzKPOfDRgz4D4ewPRogYpl";
    const URL = "https://api.twitter.com/2/tweets/search/recent"
    const [Tweets, setTweets] = useState('Loading...');

    const getTweets = async () => {
        const params = {
            'query': 'from:SJKxyz - is:retweet',
            'tweet.fields': 'author_id'
        }

        const res = await needle('get', URL, params, {
            headers: {
                "User-Agent": "v2RecentSearchJS",
                "Authorization": `Bearer ${token}`
            }
        })
        if (res.body) {
            return res.body;
        }
        else {
            throw new Error('Unable to fetch tweets');
        }
    }

    useEffect(() => {
        (async () => {
            const tweets = await getTweets();
            console.log(tweets);
            setTweets(tweets);
        }
        )();
    }, []);

    return (
        <div className="Widget News">
            <p>
                {Tweets}
            </p>
        </div>
    )
}

export default News;
