import { useEffect, useState } from 'react';
import '../App.css';

function News() {
    const [Tweets, setTweets] = useState('Loading...');


    //useEffect to fetch data from the API using a bearer token
    useEffect(() => {
        fetch('https://api.twitter.com/2/tweets/search/recent?query=%23iths2022', {
            headers: {
                Authorization: 'Bearer AAAAAAAAAAAAAAAAAAAAALjMdQEAAAAA4W09iav1RxWbqapsKhbL0Al6rrU%3DWur6GkBuFKtGFmpjDX0mn90uxTN4yzKPOfDRgz4D4ewPRogYpl',
            }
        })
            .then(res => res.json())
            .then(data => {
                setTweets(data.data);
                console.log(Tweets)
            })
            .catch(err => console.log(err));
    }, []);


    return (
        <div className="Widget News">
            <p>
                {Tweets[0].text}
            </p>
        </div>
    )
}

export default News;
